import "./App.css";

import Editor from "./Editor";
import { useState } from "react";
import React from "react";
// import './modules';

export interface IProps {}

export interface IState {
  text: string;
}

class App extends React.PureComponent<Readonly<IProps>, IState> {
  constructor(props: Readonly<IProps>) {
    super(props);
    this.state = { text: "" };
  }

  setText = (text: string) => {
    this.setState({ text });
  };

  render() {
    return (
      <div>
        <Editor theme={"bubble"} setText={this.setText} />
        <div className="text_html">{this.state.text}</div>
      </div>
    );
  }
}

export default App;
