import React from "react";
import Quill from "quill";
import hljs from "highlight.js";
import EditorStyles from "./Styles";
import * as Emoji from "quill-emoji";

import { NodeHtmlMarkdown } from "node-html-markdown";

hljs.configure({
  languages: ["javascript", "python"],
});

const BlockEmbed = Quill.import("blots/block/embed");

const highlightCode = (text: string) => hljs.highlightAuto(text).value;

class CustomCode extends BlockEmbed {
  static blotName = "code-custom";
  static tagName = "pre";
  static className = "ql-syntax";

  static create(value: any) {
    let node = super.create(value);
    const code = document.createElement("code");
    code.innerHTML = highlightCode(value);
    node.appendChild(code);
    return node;
  }

  static value(node: HTMLElement) {
    console.log("Requesting a value: ", node.textContent);
    return node.textContent;
  }
}

export interface IProps {
  theme: string;
  setText: (text: string) => void;
  converter?: NodeHtmlMarkdown;
}
export interface IState {}

class Editor extends React.PureComponent<Readonly<IProps>, IState> {
  #quill: any;
  constructor(props: Readonly<IProps>) {
    super(props);
    this.state = {};
    Quill.register(CustomCode);
    Quill.register("modules/emoji", Emoji);
  }

  componentDidMount() {
    this.#quill = new Quill("#editor-container", {
      modules: {
        toolbar: {
          container: [
            [{ header: [1, 2, false] }],
            ["bold", "italic", "underline"],
            ["image", "code-block"],
            ["emoji"],
            ["code-custom"],
          ],
          handlers: { emoji: function () {} },
        },
        "emoji-toolbar": this.props.theme === "snow",
        "emoji-textarea": this.props.theme === "bubble",
        "emoji-shortname": true,
      },
      placeholder: "Compose an epic...",
      theme: this.props.theme, // or 'bubble'
    });
  }

  handleSubmit = () => {
    if (this.props.converter) {
      this.props.setText(
        this.props.converter.translate(this.#quill.root.innerHTML)
      );
    } else {
      this.props.setText(this.#quill.root.innerHTML);
    }
  };

  render() {
    return (
      <EditorStyles>
        <div className="editor__wrap">
          <div id="editor-container"></div>
        </div>
        <button className="submit__button" onClick={this.handleSubmit}>
          Submit
        </button>
      </EditorStyles>
    );
  }
}

export default Editor;
